pragma solidity 0.5.12;

contract ConFlip{

  address public contractOwner;

  struct Bet{
    address player;
    bool headOrTail;
    uint amount;
    bool result;
  }
  mapping(address => Bet) private bets;

  constructor() public {
    contractOwner = msg.sender;
  }

  function placeBet(bool _headOrTail) public payable {
    require(msg.value >=0);
    Bet memory newBet = Bet(msg.sender, _headOrTail, msg.value, false);
    bets[msg.sender] = newBet;
  }

  function randomFlip()public returns(uint256){
    uint256 result = now%2;
    if(result == 1){
      bets[msg.sender].result = true;
    }else{
      bets[msg.sender].result = false;
    }
  }

}
